'use strict';

import React, { Component } from 'react';

import {
  StyleSheet,
  View,
  Text,
  Image,
  TextInput,
  TouchableOpacity,
  ListView,
  Linking
} from 'react-native';
import {Size,baseColor,iosMargin} from "../Tools/constStr.js";
var adata=[{data:'A'},{data:'B'},{data:'C'},{data:'D'},{data:'E'},{data:'F'},{data:'G'},{data:'H'},{data:'J'},{data:'K'},{data:'L'},{data:'M'},{data:'N'},{data:'O'},{data:'P'},{data:'Q'},{data:'R'},{data:'S'},{data:'T'},{data:'U'},{data:'V'},{data:'W'},{data:'X'},{data:'Y'},{data:'Z'},{data:'#'}]
import Contacts from 'react-native-contacts';
const ds = new ListView.DataSource({rowHasChanged: (r1, r2) => r1 !== r2});

class phone extends Component {
  constructor(props) {
    super(props);
    this.contactData=new Array();
    this.state = {
      search:"",
      dataSource:ds,
    };
  }

   getContacts(){
      Contacts.getAll((err, contacts) => {
        if(err === 'denied'){
          // error
        } else {
          console.log("contacts:"+JSON.stringify(contacts));
          this.createContactData(contacts);
        }
      })
    }

    /**
     * [createContactData description]
     * @param  {[type]} contacts [筛选后的联系人数据]
     * @return {[type]}          [description]
     */
     createContactData(contacts){

           for(let i=0;i<27;i++){
             this.contactData[i]=new Array();
             if(i==26){
               for(let j=0;j<contacts.length;j++){
                 if(this.isNumber(this.getPY(contacts[j].familyName[0]))){
                   this.contactData[i].push({givenName:contacts[j].familyName+contacts[j].givenName,phoneNumber:contacts[j].phoneNumbers[0].number});
                 }
               }
             }
             for(let j=0;j<contacts.length;j++){
               if(this.isCapital(this.getPY(contacts[j].familyName[0])).charCodeAt()==i+97){
                 this.contactData[i].push({givenName:contacts[j].familyName+contacts[j].givenName,phoneNumber:contacts[j].phoneNumbers[0].number});
               }
             }
           }
           console.log("createContactData:1"+JSON.stringify(this.contactData));
           let list=new Array();
           for(let k=0;k<this.contactData.length;k++){
             if(this.contactData[k].length!=0){
               list.push(this.contactData[k]);
             }
           }
            this.contactData=list;
           console.log("createContactData:2"+JSON.stringify(this.contactData));
               this.setState({
                 loaded:true,
                 dataSource:ds.cloneWithRows(this.contactData),
               });

         }

    isCapital(str){
        if(str.charCodeAt()>=65 && str.charCodeAt()<=90){
          return String.fromCharCode(str.charCodeAt()+32);
        }
        return str;
     }

    isNumber(str){
       if(!(str.charCodeAt()>=65 && str.charCodeAt()<=90) && !(str.charCodeAt()>=97 && str.charCodeAt()<=122)){
         return true;
       }
       return false;
     }

    getPY(str){
        var ret = '';
        for(var i=0,len=str.length;i<len;i++)
        {
            var ch = str.charAt(i);
            ret +=this.CheckCh(ch);
        }

        return ret;
    }

    CheckCh(ch){
        var uni = ch.charCodeAt(0);
        var strChineseFirstPY = '';
        //如果不在汉字处理范围之内,返回原字符
        if(uni > 40869 || uni < 19968)
            return ch;
        return strChineseFirstPY.charAt(uni-19968);
    }

  componentDidMount() {
    this.getContacts();
  }

  srollTo(char){
    console.log("srollTo111");
     let index=0;
     let j=0;
     //console.log("srollTo:"+JSON.stringify(this.contactData));
     for(let i=0;i<this.contactData.length;i++){
       if(this.contactData[i].length!=0&&String.fromCharCode(this.isCapital(this.getPY(this.contactData[i][0].givenName[0])).charCodeAt()-32)==char){
           index=i;
           break;
       }
       if(this.isNumber(this.getPY(this.contactData[i][0].givenName[0]))){
         index=i;
         break;
       }
     }
     let sum=0;
     for(let i=0;i<index;i++){
       for(let j=0;j<this.contactData[i].length;j++){
         sum++;
       }
     }
     //console.log("srollTo:index="+index);
     //console.log("srollTo:sum="+sum);
     //console.log("srollTo:"+parseInt(60*sum+index*30));
     let sTo=60*sum+index*30;
     if((sTo!=0 && sTo>=0) || (sTo==0 && char=='A')){
       this._listview.scrollTo({x: 0, y:sTo, animated: true})
       sum=0;
     }
   }

  renderRow(rowData,i,j){
      //console.log("renderRow:"+JSON.stringify(rowData));
      //console.log("renderRow:2"+this.getPY(rowData.length>0&&this.getPY(rowData[0].givenName[0])));
      //console.log("renderRow:3"+String.fromCharCode(rowData.length>0&&this.getPY(rowData[0].givenName[0]).charCodeAt()-32));
      return(
        <View style={styles.listItemView}>
          {rowData.length!=0&&<View style={styles.charView}>
            <Text style={styles.charText}>
              {this.isNumber(this.getPY(rowData[0].givenName[0]))?"#":String.fromCharCode(this.isCapital(this.getPY(rowData[0].givenName[0])).charCodeAt()-32)}
            </Text>
          </View>}
          {rowData.length!=0&&rowData.map((item,i)=>{
              return(
                <TouchableOpacity activeOpacity={0.8} style={styles.textItem} key={i} onPress={()=>{
                  //console.log(item)
                  Linking.canOpenURL('tel:'+item.phoneNumber).then(
                    (supported)=>{
                      if(supported){
                        Linking.openURL('tel:'+item.phoneNumber)
                      }else{
                        alert('通话失败')
                      }
                    }
                  )
                }}>
                  <Image style={styles.touxiang} source={require('../icon/1.png')}/>
                  <Text style={{flex:1,marginLeft:10,color:'#000000',fontSize:Size(18)}}>{item.givenName}</Text>
                </TouchableOpacity>
                );
          })}
        </View>
        );
    }


  render() {
    return (
      <View style={styles.container}>
        <View style={styles.nav}>
          <View style={styles.top_view}>
               <View style={[styles.top_item,{backgroundColor:'#769fda'}]}>
                <Text style={styles.top_item_text}>好友</Text>
              </View>
              <View style={[styles.top_item,{borderTopRightRadius:5,borderBottomRightRadius:5}]}>
                <Text style={styles.top_item_text}>通话记录</Text>
              </View>
          </View>
        </View>
        <View style={styles.search_view}>
           <Image style={styles.search_img} source={require("../icon/search.png")} />
           <TextInput
              placeholder="搜索好友" 
              style={styles.textInput}
              onChangeText={(text) => this.setState({search:text})}
              placeholderTextColor={'#e9e9e9'}
           />
        </View>
        <View style={styles.midview}>
            <View style={styles.midview_item}>
                <Image style={styles.mid_img} source={require("../icon/newFriend.png")} />
                <Text style={styles.mid_text}>新的朋友</Text>
            </View>
            <View style={{backgroundColor:"#e0e0e0",height:0.5}}/>
            <View style={styles.midview_item}>
                <Image style={styles.mid_img} source={require("../icon/recentContact.png")} />
                <Text style={styles.mid_text}>最近联系</Text>
            </View>
        </View>
        <View  style={styles.content}>
          <ListView
              style = {{marginBottom:37}}
              dataSource={this.state.dataSource}
              renderRow={this.renderRow.bind(this)}
              ref={(c) => this._listview = c}
            />
        </View>
        <View style={styles.LetterList}>
          {adata.map((item,i)=>{
            return(<TouchableOpacity  onPress={()=>this.srollTo(item.data)} key={i}><Text key={i} style={{fontSize:Size(16)}}>{item.data}</Text></TouchableOpacity>)
          })}
        </View>
      </View>
    );
  }
}

